import React, {useState} from 'react'
import { BrowserRouter, Link, Route, Switch } from 'react-router-dom'
import './App.scss'
import Header from './components/Header'
import Text from './components/Text'
import Keywords from './components/Keywords'
import Home from './components/Home'

const endPoint = process.env.REACT_APP_API_URI || 'http://localhost:3001'

export default function App() {
  const [keywords, setKeywords] = useState([])

  // useEffect(function () {
  //   setS(["a", "b", "c", "d", "e", "f"])
  // }, [])

  const fetchData = async () => {
    try {
      const response = await fetch(endPoint + '/keywords', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
         },
        body: JSON.stringify({ 
          text: window.document.getElementById('text').value
        }) 
      });

      console.log(window.document.getElementById('text').value)
      // console.log(response)
      const data = await response.json();
      // console.log(data)
      console.log(data.keywords)
      setKeywords(data.keywords)
      // window.document.getElementById('keywords').innerHTML = ''
      // for (var key in data.keywords)
      //   window.document.getElementById('keywords').innerHTML += '<button className="patata">' + data.keywords[key] + '</button>'
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <Header />
      <BrowserRouter>
        <main className="App-main">
          <Switch>
            <Route path='/' element={
              <Home />
            }/>
            <Route path='/text' element={
              <Text />
            }/>
            <Route path='/keywords'element={
              <Keywords />
            }/>
            {/* <Route path='/questions'>
              <Questions />
            </Route> */}
          </Switch>
          <div className='keywords'>
            <p>Keywords encontradas:</p>
            <div id='keywords'>
              {
                keywords.map(name => <button>{name}</button>)
              }
            </div>
          </div>
          <Link to='/keywords' onClick={fetchData}>
            Search Keywords
          </Link>
        </main>
      </BrowserRouter>
    </div>
  )
}
