import React, {useState} from 'react'
import { BrowserRouter, Link, Routes, Route } from 'react-router-dom'
import './App.scss'
import Header from './components/Header'
// import Text from './components/Text'
// import Keywords from './components/Keywords'
import Home from './components/Home'

const endPoint = process.env.REACT_APP_API_URI || 'http://localhost:3001'

export default function App() {
  const [keywords, setKeywords] = useState([])

  const fetchData = async () => {
    try {
      const response = await fetch(endPoint + '/keywords', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
         },
        body: JSON.stringify({ 
          text: window.document.getElementById('text').value
        }) 
      });
      const data = await response.json();
      console.log(data.keywords)
      setKeywords(data.keywords)
    } catch (error) {
      console.log(error);
    }
  };

  const fetchQuestions = async () => {
    try {
      const response = await fetch(endPoint + '/keywords', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
         },
        body: JSON.stringify({ 
          text: window.document.getElementById('text').value
        }) 
      });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <Header />
      <BrowserRouter>
        <main className="App-main">
          <Routes>
            <Route path='/' element={
              <Home />
            }/>
            <Route path='/text'element={
              // <Text />
              <div className='textSide'>
                <p>
                    Aqu√≠ pegas tu texto:
                </p>
                <textarea id='text' className='textArea'></textarea>
                <Link to='/keywords' onClick={fetchData}>
                    Search Keywords
                </Link>
              </div>
            }/>
            <Route path='/keywords'element={
              // <Keywords keywords={keywords}/>
              <div className='keywords'>
                <p>Keywords encontradas:</p>
                <div id='keywords'>
                    {
                        keywords.map(key => <button key={key}>{key}</button>)
                    }
                </div>
                <Link to='/questions' onClick={fetchQuestions}>
                    Make Questions
                </Link>
              </div>
            }/>
            {/* <Route path='/questions'>
              <Questions />
            </Route> */}
          </Routes>


        </main>
      </BrowserRouter>
    </div>
  )
}
