import React, {useEffect,useState} from 'react'
// import {Link,  Route} from 'wouter'
import logo from './logo.svg'
import './App.scss'

const endPoint = process.env.REACT_APP_API_URI || 'http://localhost:3001'
const fetchData = async () => {
  try {
    const response = await fetch(endPoint + '/keywords', {
      method: 'POST',
      headers: { 
        'Content-Type': 'application/json'
       },
      body: JSON.stringify({ 
        text: window.document.getElementById('text').value
      }) 
    });

    console.log(window.document.getElementById('text').value)
    // console.log(response)
    const data = await response.json();
    // console.log(data)
    console.log(data.keywords)
    window.document.getElementById('keywords').innerHTML = ''
    for (var key in data.keywords) {
      // console.log(data.keywords[key])
      window.document.getElementById('keywords').innerHTML += '<button className="patata">' + data.keywords[key] + '</button>'
      // console.log(window.document.getElementById('keywords').textContent)
    }
    // window.document.getElementById('keywords').value = data.keywords
  } catch (error) {
    console.log(error);
  }
};

var keywords = ["micono", "cono", "yo", "azul"]

export default function App() {
  // const [s, setS] = useState(keywords)

  // useEffect(function () {
  //   setS(["a", "b", "c", "d", "e", "f"])
  // }, [])

  return (
    <div className="App">
      <header className='App-header'>
        <img src={logo} className="App-logo" alt="logo" />
        HistKey
      </header>
      <main className="App-main">
        <div className='textSide'>
          <p>
            Aqu√≠ pegas tu texto:
          </p>
          <textarea id='text' className='textArea'></textarea>
          <button onClick={fetchData} className='searchButton'>Buscar Keywords</button>
        </div>
        <div className='keywords'>
          <p>Keywords encontradas:</p>
          <div id='keywords'></div>
        </div>
        {
          keywords.map(name => <button>{name}</button>)
        }
      </main>
    </div>
  )
}
