import React, {useState} from 'react'
import { BrowserRouter, Link, Routes, Route } from 'react-router-dom'
import './App.scss'
import Header from './components/Header'
// import Text from './components/Text'
// import Keywords from './components/Keywords'
import Home from './components/Home'
// import Question from './components/Question'

const endPoint = process.env.REACT_APP_API_URI || 'http://localhost:3001'
var text = ''

export default function App() {
  const [keywords, setKeywords] = useState([])
  const [questions, setQuestions] = useState([])

  const fetchData = async () => {
    try {
      text = window.document.getElementById('text').value
      const response = await fetch(endPoint + '/keywords', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
         },
        body: JSON.stringify({ 
          theText: text
        }) 
      });
      console.log(text)
      const data = await response.json();
      // console.log(data.keywords)
      setKeywords(data.keywords)
    } catch (error) {
      console.log(error);
    }
  };

  const fetchQuestions = async () => {
    try {
      console.log(text)
      const response = await fetch(endPoint + '/questions', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json'
         },
        body: JSON.stringify({ 
          theText: text,
          keywords: keywords
        }) 
      });
      console.log(text)
      console.log(response.json())
      const data = await response.json();
      console.log(data.questions)
      setQuestions(data.questions)
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <Header />
      <BrowserRouter>
        <main className="App-main">
          <Routes>
            <Route path='/' element={
              <Home />
            }/>
            <Route path='/text' element={
              // <Text />
              <div className='textSide'>
                <p>
                    Aqu√≠ pegas tu texto:
                </p>
                <textarea id='text' className='textArea'></textarea>
                <Link to='/keywords' onClick={fetchData} className='link'>
                    Search Keywords
                </Link>
              </div>
            }/>
            <Route path='/keywords' element={
              // <Keywords keywords={keywords}/>
              <div className='keywords'>
                <p>Founded Keywords:</p>
                <div className='keywords-container'>
                  {
                    keywords.map(key => <button key={key} className='key-button'>{key}</button>)
                  }
                </div>
                <Link to='/questions' onClick={fetchQuestions} className='link'>
                    Make Questions
                </Link>
              </div>
            }/>
            <Route path='/questions' element={
              // <Question />
              <div>
                <p>Questions maked:</p>
                <div>
                  {
                    questions.map(q => <p key={q}>{q}</p>)
                  }
                </div>
                <Link to='/' className='link'>
                    Finnish Process
                </Link>
              </div>
            }/>
          </Routes>


        </main>
      </BrowserRouter>
    </div>
  )
}
